import './App.css';
import Axios from "axios";
import React, { useEffect, useState } from 'react';



function App() {

  const [catFact, setCatFact] = useState('');
  const [dogFact, setDogFact] = useState('');

  const fetchCatFact = () =>{
    Axios.get('https://catfact.ninja/fact')
      .then((res) => {
        setCatFact(res.data.fact);
      });
  }

  const fetchDogFact = () => {
    Axios.get('https://dog-api.kinduff.com/api/facts')
      .then((res) => {
        const randomIndex = Math.floor(Math.random() * res.data.facts.length);
        const randomFact = res.data.facts[randomIndex];
        setDogFact(randomFact);
      })
  }


  useEffect(() =>{
    Axios.get('https://catfact.ninja/fact')
      .then((res) => {
        setCatFact(res.data.fact);
      });
  },[]);

  useEffect(() =>{
    Axios.get('https://dog-api.kinduff.com/api/facts')
      .then((res) => {
        setDogFact(res.data.fact);
      });
  },[]);



  return (
    <div className="App">
      <header className="App-header">
        <h1 className="text-3xl font-bold mb-4">Pet Facts Generator</h1>

        <p className="mb-2">Do you want to learn about cats? Generate facts.</p>
        <button
          className="bg-green-500 hover:bg-green-600 text-white font-bold py-2 px-4 rounded"
          onClick={fetchCatFacts}
        >
          Generate Cat Facts
        </button>
        {/* Mapping through catFacts array to display multiple facts */}
        {catFacts.map((fact, index) => (
          <p key={index} className="mt-2">{fact.fact}</p>
        ))}

        <p className="mt-4 mb-2">Do you want to learn about dogs? Generate facts.</p>
        <button
          className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded"
          onClick={fetchDogFact}
        >
          Generate Dog Fact
        </button>
        {/* Conditionally render the dog fact */}
        {dogFact && <p className="mt-2">{dogFact}</p>}
      </header>
    </div>
  );
}



export default App;
