import './App.css';
import Axios from 'axios';
import React, { useEffect, useState } from 'react';

function App() {
  const [catFact, setCatFact] = useState('');
  const [dogFact, setDogFact] = useState('');

  const fetchCatFact = () => {
    Axios.get('https://catfact.ninja/fact')
      .then((res) => {
        setCatFact(res.data.fact);
      })
      .catch((error) => {
        console.error('Error fetching cat fact:', error);
      });
  };

  const fetchDogFact = () => {
    Axios.get('https://dog-api.kinduff.com/api/facts')
      .then((res) => {
        const randomIndex = Math.floor(Math.random() * res.data.facts.length);
        const randomFact = res.data.facts[randomIndex];
        setDogFact(randomFact);
      })
      .catch((error) => {
        console.error('Error fetching dog fact:', error);
      });
  };

  useEffect(() => {
    fetchCatFact();
  }, []);

  useEffect(() => {
    fetchDogFact();
  }, []);

  return (
    <div className="App min-h-screen bg-blue-100 flex flex-col items-center justify-center">
      <header className="text-center">

        <h1 className="text-4xl font-bold mb-4 text-green-800">Pet Facts Generator</h1>
        <h2 className="mb-4">
          Do you have a pet? Well, it doesn't matter, learn about cats and dogs now by
          generating facts.
        </h2>

        <div className="my-4">
          <h3 className="text-xl font-semibold mb-2">Do you want to learn about cats? Generate facts.</h3>
          <button onClick={fetchCatFact} className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded shadow">
            Generate Cat Fact
          </button>
          <p className="text-green-800 mt-2">{catFact}</p>
        </div>
